mapper.insert:
insert into  notice (
				  not_no
				, board
				, subject
				, content
				, no_date
				, not_file
				, noti_flag
            )values(
                 notice_num_seq.nextval
                 ,#{board}
                 ,#{subject}
                 ,#{content}
                 ,sysdate
                 ,'noFile'
				 ,#{noti_flag}

                 )

mapper.update:
		update 
		      notice 
		   set 
		       board = #{board}
		     , subject=#{subject}
		     ,content=#{content}
		     ,no_date=sysdate      	     

              ,not_file = #{not_file, jdbcType=VARCHAR}
              		      	      	     
		     ,noti_flag = #{noti_flag, jdbcType=VARCHAR}

		 where 
		      not_no=#{not_no}
